import 'bike_details.dart';import 'user_details.dart';import 'trip_details.dart';class Dashboard {  List<Bike_details> bikeDetails;  List<User_details> userDetails;  List<Trip_details> tripDetails;  Dashboard({      this.bikeDetails,       this.userDetails,       this.tripDetails});  Dashboard.fromJson(dynamic json) {    if (json['bike_details'] != null) {      bikeDetails = [];      json['bike_details'].forEach((v) {        bikeDetails.add(Bike_details.fromJson(v));      });    }    if (json['user_details'] != null) {      userDetails = [];      json['user_details'].forEach((v) {        userDetails.add(User_details.fromJson(v));      });    }    if (json['trip_details'] != null) {      tripDetails = [];      json['trip_details'].forEach((v) {        tripDetails.add(Trip_details.fromJson(v));      });    }  }  Map<String, dynamic> toJson() {    var map = <String, dynamic>{};    if (bikeDetails != null) {      map['bike_details'] = bikeDetails.map((v) => v.toJson()).toList();    }    if (userDetails != null) {      map['user_details'] = userDetails.map((v) => v.toJson()).toList();    }    if (tripDetails != null) {      map['trip_details'] = tripDetails.map((v) => v.toJson()).toList();    }    return map;  }}