/// status : "1"/// msg : "User Details"/// name : "ani"/// image : "/media/profile_picture/user_profile_2021-07-21_1521.jpg"class GetUserNameImageResponse {  String status;  String msg;  String name;  String image;  GetUserNameImageResponse({      this.status,       this.msg,       this.name,       this.image});  fromJson(dynamic json) {    status = json["status"];    msg = json["msg"];    name = json["name"];    image = json["image"];  }  Map<String, dynamic> toJson() {    var map = <String, dynamic>{};    map["status"] = status;    map["msg"] = msg;    map["name"] = name;    map["image"] = image;    return map;  }}