class Trip_details {  String passengerId;  String passengerName;  String phoneNo;  String price;  String fromAddress;  String fromLat;  String fromLong;  String toLat;  String toLong;  String tripStatus;  Trip_details({      this.passengerId,       this.passengerName,       this.phoneNo,       this.price,       this.fromAddress,       this.fromLat,       this.fromLong,       this.toLat,       this.toLong,       this.tripStatus});  Trip_details.fromJson(dynamic json) {    passengerId = json['passenger_id'];    passengerName = json['passenger_name'];    phoneNo = json['phone_no'];    price = json['price'];    fromAddress = json['from_address'];    fromLat = json['from_lat'];    fromLong = json['from_long'];    toLat = json['to_lat'];    toLong = json['to_long'];    tripStatus = json['trip_status'];  }  Map<String, dynamic> toJson() {    var map = <String, dynamic>{};    map['passenger_id'] = passengerId;    map['passenger_name'] = passengerName;    map['phone_no'] = phoneNo;    map['price'] = price;    map['from_address'] = fromAddress;    map['from_lat'] = fromLat;    map['from_long'] = fromLong;    map['to_lat'] = toLat;    map['to_long'] = toLong;    map['trip_status'] = tripStatus;    return map;  }}