class User_details {  String firstName;  String lastName;  String gender;  String birthDate;  String mobileNo;  String email;  String profileImg;  String bloodGroup;  User_details({      this.firstName,       this.lastName,       this.gender,       this.birthDate,       this.mobileNo,       this.email,       this.profileImg,       this.bloodGroup});  User_details.fromJson(dynamic json) {    firstName = json['first_name'];    lastName = json['last_name'];    gender = json['gender'];    birthDate = json['birth_date'];    mobileNo = json['mobile_no'];    email = json['email'];    profileImg = json['profile_img'];    bloodGroup = json['blood_group'];  }  Map<String, dynamic> toJson() {    var map = <String, dynamic>{};    map['first_name'] = firstName;    map['last_name'] = lastName;    map['gender'] = gender;    map['birth_date'] = birthDate;    map['mobile_no'] = mobileNo;    map['email'] = email;    map['profile_img'] = profileImg;    map['blood_group'] = bloodGroup;    return map;  }}